from qcloudsms_py import SmsSingleSenderfrom qcloudsms_py.httpclient import HTTPErrorimport sslclass MySMS:    def __init__(self):        # 全局消除ssl证书校验        ssl._create_default_https_context = ssl._create_unverified_context        data = open('sms_tool.md', 'r')        # line = data.read()        config_data = data.readlines()        data.close()        print(config_data)        self.appid = 1400080597        self.appkey = "2ba0bba2b0b6f85c46ce33a968f6637b"        self.phone_numbers = config_data    def send_sms_one(self, message):        """        单发短信        :param message: 短信内容        :return:        """        ssender = SmsSingleSender(self.appid, self.appkey)        result = ''        try:            result = ssender.send(0, 86, self.phone_numbers[0], message)        except HTTPError as e:            print(e)        except Exception as e:            print(e)        print(result)    def send_sms_many(self, message):        """        群发短信        :param message: 短信内容        :return:        """        ssender = SmsSingleSender(self.appid, self.appkey)        result = ''        try:            result = ssender.send(0, 86, self.phone_numbers, message)        except HTTPError as e:            print(e)        except Exception as e:            print(e)        print(result)